import type { SocialNetworksType } from './ModalSpeaker'

type SocialIconsProps = {
  socialNetworks: Array<SocialNetworksType>
}

export default function SocialIcons({ socialNetworks }: SocialIconsProps) {
  const icons = {
    facebook: <Facebook />,
    instagram: <Instagram />,
    linkedin: <Linkedin />,
    web: <Web />
  }

  return (
    <div className='mx-auto my-4 flex items-center space-x-8 md:my-8'>
      {socialNetworks.map(({ name, url }) => (
        <a key={name} href={url} target='_blank' rel='noopener noreferrer'>
          {icons[name]}
        </a>
      ))}
    </div>
  )
}

